import * as React from 'react';
import { Text, View, StyleSheet, ScrollView } from 'react-native';
import { NavigationContainer } from '@react-navigation/native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { Button, Card, Title, Paragraph } from 'react-native-paper';
import { MaterialCommunityIcons } from '@expo/vector-icons';


function HomeScreen({ navigation }) {
  return (
    <>
      <ScrollView>

        <View>
          <Card style={styles.card}>
            <Card.Content >
              <Title>Nombre del parque</Title>
              <Paragraph>HORARIO: 8:00 - 18:00</Paragraph>
              <Paragraph>DIRECCION: Calle ejemplo</Paragraph>
            </Card.Content>
            <Card.Cover source={{ uri: 'https://picsum.photos/700' }} />
            <Card.Actions style={styles.buttonSection}>
              <Button color='#000' style={styles.button} onPress={() => navigation.navigate('InfoScreen')} >Más Información</Button>
            </Card.Actions>
          </Card>
        </View>

        <View>
          <Card style={styles.card}>
            <Card.Content >
              <Title>Nombre del parque</Title>
              <Paragraph>HORARIO: 8:00 - 18:00</Paragraph>
              <Paragraph>DIRECCION: Calle ejemplo</Paragraph>
            </Card.Content>
            <Card.Cover source={{ uri: 'https://picsum.photos/700' }} />
            <Card.Actions style={styles.buttonSection}>
              <Button color='#000' style={styles.button} onPress={() => navigation.navigate('InfoScreen')} >Más Información</Button>
            </Card.Actions>
          </Card>
        </View>

      </ ScrollView>
    </>
  );
};

function ActivityScreen() {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Here goes Login screen/profile screen!</Text>
    </View>
  );
};

function SearchScreen() {
  return (
      <View style={styles.view}>
        <Text>Here</Text>
      </View>
    );
};

function InfoScreen(navigation) {
  return (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      <Text>Here goes InfoScreen !</Text>
    </View>
  );
};


const Tab = createBottomTabNavigator();
const Stack = createNativeStackNavigator();

function Home() {
  return (
    <Tab.Navigator initialRouteName="HomeScreen" screenOptions={{tabBarActiveTintColor: '#e91e63',}}>
            <Tab.Screen name="HomeScreen" component={HomeScreen} options={{ 
              title: 'Lista de Parques',
              headerStyle:{ backgroundColor: '#A7D8F6'},
              tabBarIcon: ({ color, size}) => (
                <MaterialCommunityIcons name="home" color={color} size={size} />
                ),
              }} />
            <Tab.Screen name="SearchScreen" component={SearchScreen} options={{ 
              title: 'Buscar por Nombre', 
              headerStyle:{ backgroundColor: '#A7D8F6'},
              tabBarIcon: ({ color, size}) => (
                <MaterialCommunityIcons name="search" color={color} size={size} />
                ),
              }} />
            <Tab.Screen name="ActivityScreen" component={ActivityScreen} options={{ title: 'Buscar por Actividad', headerStyle:{ backgroundColor: '#A7D8F6'}}} />
    </Tab.Navigator>
  )
};

export default function App() {
  return (
        <NavigationContainer>
          <Stack.Navigator>
           <Stack.Screen name="Home" component={Home} options={{headerShown: false}} />
            <Stack.Screen name="InfoScreen" component={InfoScreen} options={{ title: 'Información del Parque', headerStyle:{ backgroundColor: '#A7D8F6'} }} />
          </Stack.Navigator>
        </NavigationContainer>
  );
};

const styles = StyleSheet.create({
  card: {
    marginTop: 25,
  },
  buttonSection: {
    justifyContent: 'center',
    alignItems: 'center',
  },
  button: {
    backgroundColor: '#a9daf6',
  }
});
